package com.crm.formation;

/**
 * @author Joanna
 *
 */
public class SwitchTest {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int choix = 37;
		switch (choix) {
		case 37:
			System.out.println("Café ?");
			break;
		case 45:
			System.out.println("Thé ?");
			break;
		default:
			System.out.println("???");
			break;
		}
	}

}
